// Polyfills for web (must be before all other imports)
import 'core-js/stable';
import 'regenerator-runtime/runtime';
import process from 'process';
import { Buffer } from 'buffer';

// Make Node-like globals available for dependencies expecting them
// @ts-ignore
if (!(globalThis as any).process) (globalThis as any).process = process;
// @ts-ignore
if (!(globalThis as any).Buffer) (globalThis as any).Buffer = Buffer;
// Minimal hrtime shim used by webpack-dev-server logger
// @ts-ignore
if (!process.hrtime) (process as any).hrtime = () => [0, 0] as [number, number];

import { Video as ExpoVideo } from 'expo-av';
import * as ImagePicker from 'expo-image-picker';
import { X as CloseIcon, Heart, Image as ImageIcon, MapPin, MessageCircle, Send, Settings, User, Video, X } from 'lucide-react-native';
import React, { useEffect, useRef, useState } from 'react';
import { Animated, Dimensions, Image, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';

const SCREEN_WIDTH = Dimensions.get('window').width;
const SWIPE_THRESHOLD = SCREEN_WIDTH * 0.25;
const SWIPE_OUT_DURATION = 250;

const LoveConnectApp = () => {
    const [currentScreen, setCurrentScreen] = useState('discover');
    const [currentProfileIndex, setCurrentProfileIndex] = useState(0);
    const [matches, setMatches] = useState([
        {
            id: 1,
            name: "Isabella",
            image: 'https://randomuser.me/api/portraits/women/44.jpg',
            lastMessage: "¡Hola! ¿Cómo estás?",
            time: "10:30 AM"
        },
        {
            id: 2,
            name: "Carlos",
            image: 'https://randomuser.me/api/portraits/men/32.jpg',
            lastMessage: "¿Quedamos esta tarde?",
            time: "Ayer"
        }
    ]);
    const [chatMessages, setChatMessages] = useState({
        1: [
            { id: 1, text: "¡Hola! ¿Cómo estás?", sent: false, time: "10:30 AM" },
            { id: 2, text: "¡Hola! Estoy bien, ¿y tú?", sent: true, time: "10:32 AM" },
            { id: 3, text: "¿Te gustaría salir a tomar algo?", sent: false, time: "10:33 AM" },
        ],
        2: [
            { id: 1, text: "¡Hola! ¿Quedamos hoy?", sent: false, time: "Ayer" },
            { id: 2, text: "¡Claro! ¿A qué hora?", sent: true, time: "Ayer" },
            { id: 3, text: "¿Qué tal a las 8?", sent: false, time: "Ayer" },
        ]
    });
    const [selectedChat, setSelectedChat] = useState(null);
    const [newMessage, setNewMessage] = useState('');
    const [selectedMedia, setSelectedMedia] = useState(null);
    const [mediaType, setMediaType] = useState(null); // 'image' or 'video'

    // Perfiles de ejemplo
    const profiles = [
        {
            id: 1,
            name: "Isabella",
            age: 24,
            location: "Madrid",
            distance: "2 km",
            bio: "Amante del arte y los viajes. Fotógrafa en mis tiempos libres",
            image: 'https://randomuser.me/api/portraits/women/44.jpg'
        },
        {
            id: 2,
            name: "Carlos",
            age: 28,
            location: "Barcelona",
            distance: "5 km",
            bio: "Programador y amante de la naturaleza",
            image: 'https://randomuser.me/api/portraits/men/32.jpg'
        }
    ];

    // Manejadores de acciones
    const handleLike = () => {
        const currentProfile = profiles[currentProfileIndex];
        const newMatch = {
            id: currentProfile.id,
            name: currentProfile.name,
            image: currentProfile.image,
            lastMessage: "¡Tenéis un match!",
            time: "Ahora"
        };

        setMatches([...matches, newMatch]);

        // Inicializar mensajes vacíos para el nuevo match
        setChatMessages(prev => ({
            ...prev,
            [currentProfile.id]: [
                {
                    id: 1,
                    text: "¡Hola! Ahora podéis chatear.",
                    sent: false,
                    time: "Ahora"
                }
            ]
        }));

        goToNextProfile();
    };

    const handleDislike = () => {
        goToNextProfile();
    };

    const goToNextProfile = () => {
        if (currentProfileIndex < profiles.length - 1) {
            setCurrentProfileIndex(currentProfileIndex + 1);
        } else {
            // Volver al primer perfil si llegamos al final
            setCurrentProfileIndex(0);
        }
    };

    // Enviar un nuevo mensaje
    const pickMedia = async (type) => {
        try {
            let result;
            if (type === 'image') {
                result = await ImagePicker.launchImageLibraryAsync({
                    mediaTypes: ImagePicker.MediaTypeOptions.Images,
                    allowsEditing: true,
                    aspect: [4, 3],
                    quality: 0.7,
                });
            } else if (type === 'video') {
                result = await ImagePicker.launchImageLibraryAsync({
                    mediaTypes: ImagePicker.MediaTypeOptions.Videos,
                    allowsEditing: true,
                    quality: 0.7,
                });
            }

            if (!result.canceled) {
                setSelectedMedia(result.assets[0].uri);
                setMediaType(type);
            }
        } catch (error) {
            console.error('Error picking media:', error);
        }
    };

    const clearSelectedMedia = () => {
        setSelectedMedia(null);
        setMediaType(null);
    };

    const handleSendMessage = async () => {
        if (!newMessage.trim() && !selectedMedia) return;

        const newMsg = {
            id: Date.now(),
            text: newMessage,
            sent: true,
            timestamp: new Date().toISOString(),
            media: selectedMedia,
            mediaType: mediaType,
        };

        setChatMessages(prev => ({
            ...prev,
            [selectedChat]: [...(prev[selectedChat] || []), newMsg]
        }));

        setNewMessage('');
        clearSelectedMedia();

        // Simular respuesta automática después de 1 segundo
        setTimeout(async () => {
            const responseMsg = {
                id: Date.now() + 1,
                text: getRandomResponse(),
                sent: false,
                timestamp: new Date().toISOString(),
            };

            setChatMessages(prev => ({
                ...prev,
                [selectedChat]: [...(prev[selectedChat] || []), responseMsg]
            }));
        }, 1000);
    };

    // Generar respuestas aleatorias
    const getRandomResponse = () => {
        const responses = [
            "¡Hola! ¿Cómo estás?",
            "¡Me encanta hablar contigo!",
            "¿Qué planes tienes para hoy?",
            "¡Qué interesante! Cuéntame más.",
            "¿Te gustaría quedar algún día?",
            "¡Jajaja, qué gracioso!"
        ];
        return responses[Math.floor(Math.random() * responses.length)];
    };

    // Referencia para el ScrollView
    const scrollViewRef = useRef<ScrollView>(null);

    // Efecto para hacer scroll al final cuando hay nuevos mensajes
    useEffect(() => {
        if (scrollViewRef.current) {
            scrollViewRef.current.scrollToEnd({ animated: true });
        }
    }, [chatMessages, selectedChat]);

    // Renderizar la pantalla de chat
    const renderChatScreen = () => {
        const messages = chatMessages[selectedChat] || [];
        const match = matches.find(m => m.id === selectedChat);

        const renderMessageContent = (message) => (
            <>
                {message.media && (
                    <View style={styles.mediaContainer}>
                        {message.mediaType === 'image' ? (
                            <Image
                                source={{ uri: message.media }}
                                style={styles.mediaPreview}
                                resizeMode="cover"
                            />
                        ) : (
                            <ExpoVideo
                                source={{ uri: message.media }}
                                style={styles.mediaPreview}
                                useNativeControls
                                isLooping
                            />
                        )}
                    </View>
                )}
                {message.text && (
                    <Text style={[
                        styles.messageText,
                        message.sent ? styles.sentMessageText : styles.receivedMessage
                    ]}>
                        {message.text}
                    </Text>
                )}
            </>
        );

        return (
            <View style={styles.chatContainer}>
                <View style={styles.chatHeader}>
                    <TouchableOpacity
                        onPress={() => setSelectedChat(null)}
                        style={styles.backButton}
                    >
                        <Text style={styles.backButtonText}>←</Text>
                    </TouchableOpacity>
                    <Text style={styles.chatTitle}>{match?.name || 'Chat'}</Text>
                    <View style={{ width: 40 }} />
                </View>
                <ScrollView
                    ref={scrollViewRef}
                    style={styles.messagesContainer}
                    contentContainerStyle={{ paddingBottom: 20 }}
                    onContentSizeChange={() => {
                        if (scrollViewRef.current) {
                            scrollViewRef.current.scrollToEnd({ animated: true });
                        }
                    }}
                >
                    {messages.map((message) => (
                        <View
                            key={message.id}
                            style={[
                                styles.messageBubble,
                                message.sent ? styles.sentMessage : styles.receivedMessage
                            ]}
                        >
                            {renderMessageContent(message)}
                            <Text style={styles.messageTime}>{message.timestamp}</Text>
                        </View>
                    ))}
                </ScrollView>

                {selectedMedia && (
                    <View style={styles.mediaPreviewContainer}>
                        {mediaType === 'image' ? (
                            <Image
                                source={{ uri: selectedMedia }}
                                style={styles.mediaThumbnail}
                                resizeMode="cover"
                            />
                        ) : (
                            <View style={[styles.mediaThumbnail, styles.videoThumbnail]}>
                                <Video size={24} color="#fff" />
                            </View>
                        )}
                        <TouchableOpacity
                            style={styles.removeMediaButton}
                            onPress={clearSelectedMedia}
                        >
                            <CloseIcon size={16} color="#fff" />
                        </TouchableOpacity>
                    </View>
                )}
                <View style={styles.inputContainer}>
                    <TouchableOpacity
                        style={styles.mediaButton}
                        onPress={() => pickMedia('image')}
                    >
                        <ImageIcon size={24} color="#FF5A5F" />
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.mediaButton}
                        onPress={() => pickMedia('video')}
                    >
                        <Video size={24} color="#FF5A5F" />
                    </TouchableOpacity>
                    <TextInput
                        style={styles.input}
                        placeholder="Escribe un mensaje..."
                        value={newMessage}
                        onChangeText={setNewMessage}
                        onSubmitEditing={handleSendMessage}
                    />
                    <TouchableOpacity
                        style={styles.sendButton}
                        onPress={handleSendMessage}
                        disabled={!newMessage.trim() && !selectedMedia}
                    >
                        <Send size={24} color={!newMessage.trim() && !selectedMedia ? "#ccc" : "#FF5A5F"} />
                    </TouchableOpacity>
                </View>
            </View>
        );
    };

    // Renderizar la lista de matches
    const renderMatchesList = () => (
        <ScrollView style={styles.matchesList}>
            {matches.map((match) => (
                <TouchableOpacity
                    key={match.id}
                    style={styles.matchItem}
                    onPress={() => setSelectedChat(match.id)}
                >
                    <Image
                        source={{ uri: match.image }}
                        style={styles.matchImage}
                    />
                    <View style={styles.matchInfo}>
                        <Text style={styles.matchName}>{match.name}</Text>
                        <Text
                            style={styles.matchLastMessage}
                            numberOfLines={1}
                            ellipsizeMode="tail"
                        >
                            {match.lastMessage}
                        </Text>
                    </View>
                    <Text style={styles.matchTime}>{match.time}</Text>
                </TouchableOpacity>
            ))}
        </ScrollView>
    );

    // Renderizar la pantalla de descubrimiento
    const renderDiscoverScreen = () => {
        const profile = profiles[currentProfileIndex];
        const position = new Animated.ValueXY();

        const onGestureEvent = Animated.event(
            [{ nativeEvent: { translationX: position.x, translationY: position.y } }],
            { useNativeDriver: true }
        );

        const onHandlerStateChange = (event) => {
            if (event.nativeEvent.oldState === State.ACTIVE) {
                const { translationX } = event.nativeEvent;

                if (translationX > SWIPE_THRESHOLD) {
                    // Swipe right - Like
                    swipeOut('right');
                } else if (translationX < -SWIPE_THRESHOLD) {
                    // Swipe left - Dislike
                    swipeOut('left');
                } else {
                    // Reset position if not enough swipe
                    Animated.spring(position, {
                        toValue: { x: 0, y: 0 },
                        useNativeDriver: true,
                    }).start();
                }
            }
        };

        const swipeOut = (direction) => {
            const x = direction === 'right' ? SCREEN_WIDTH : -SCREEN_WIDTH;

            Animated.timing(position, {
                toValue: { x, y: 0 },
                duration: SWIPE_OUT_DURATION,
                useNativeDriver: true,
            }).start(() => {
                if (direction === 'right') {
                    handleLike();
                } else {
                    handleDislike();
                }
                position.setValue({ x: 0, y: 0 });
            });
        };

        const rotate = position.x.interpolate({
            inputRange: [-SCREEN_WIDTH / 2, 0, SCREEN_WIDTH / 2],
            outputRange: ['-30deg', '0deg', '30deg'],
            extrapolate: 'clamp',
        });

        const likeOpacity = position.x.interpolate({
            inputRange: [0, SCREEN_WIDTH / 4],
            outputRange: [0, 1],
            extrapolate: 'clamp',
        });

        const nopeOpacity = position.x.interpolate({
            inputRange: [-SCREEN_WIDTH / 4, 0],
            outputRange: [1, 0],
            extrapolate: 'clamp',
        });

        return (
            <View style={styles.discoverContainer}>
                <PanGestureHandler
                    onGestureEvent={onGestureEvent}
                    onHandlerStateChange={onHandlerStateChange}
                >
                    <Animated.View
                        style={[
                            styles.profileCard,
                            {
                                transform: [
                                    { translateX: position.x },
                                    { translateY: position.y },
                                    { rotate },
                                ],
                            },
                        ]}
                    >
                        <Image
                            source={{ uri: profile.image }}
                            style={styles.profileImage}
                        />
                        <View style={styles.profileInfo}>
                            <Text style={styles.profileName}>
                                {profile.name}, {profile.age}
                            </Text>
                            <View style={styles.locationContainer}>
                                <MapPin size={16} color="#666" />
                                <Text style={styles.locationText}>
                                    {profile.location} • {profile.distance}
                                </Text>
                            </View>
                            <Text style={styles.bioText}>
                                {profile.bio}
                            </Text>
                        </View>

                        <Animated.View
                            style={[styles.likeBadge, { opacity: likeOpacity }]}
                            pointerEvents="none"
                        >
                            <Text style={styles.likeText}>LIKE</Text>
                        </Animated.View>

                        <Animated.View
                            style={[styles.nopeBadge, { opacity: nopeOpacity }]}
                            pointerEvents="none"
                        >
                            <Text style={styles.nopeText}>NOPE</Text>
                        </Animated.View>

                    </Animated.View>
                </PanGestureHandler>
                <View style={styles.actionButtons}>
                    <TouchableOpacity
                        style={[styles.actionButton, styles.dislikeButton]}
                        onPress={handleDislike}
                    >
                        <X size={32} color="#FF5A5F" />
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={[styles.actionButton, styles.likeButton]}
                        onPress={handleLike}
                    >
                        <Heart size={32} color="#4CAF50" />
                    </TouchableOpacity>
                </View>
            </View>
        );
    };

    // Renderizar la barra de navegación inferior
    const renderBottomNav = () => (
        <View style={styles.bottomNav}>
            <TouchableOpacity
                style={styles.navButton}
                onPress={() => setCurrentScreen('discover')}
            >
                <User size={24} color={currentScreen === 'discover' ? '#FF5A5F' : '#666'} />
            </TouchableOpacity>
            <TouchableOpacity
                style={styles.navButton}
                onPress={() => setCurrentScreen('messages')}
            >
                <MessageCircle size={24} color={currentScreen === 'messages' ? '#FF5A5F' : '#666'} />
            </TouchableOpacity>
            <TouchableOpacity
                style={styles.navButton}
                onPress={() => setCurrentScreen('settings')}
            >
                <Settings size={24} color={currentScreen === 'settings' ? '#FF5A5F' : '#666'} />
            </TouchableOpacity>
        </View>
    );

    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Text style={styles.headerTitle}>LoveConnect</Text>
            </View>

            <View style={styles.content}>
                {currentScreen === 'discover' && renderDiscoverScreen()}
                {currentScreen === 'messages' && (
                    selectedChat ? (
                        renderChatScreen()
                    ) : (
                        renderMatchesList()
                    )
                )}
                {currentScreen === 'settings' && (
                    <View style={styles.placeholderContainer}>
                        <Text>Ajustes</Text>
                    </View>
                )}
            </View>

            {renderBottomNav()}
        </View>
    );
};

